commits.content:
/*
Template Name: ABC Tots
Author: Ingrid Kuhn
Author URI: themeforest/user/ingridk
Version: 1.1
Last change:26th April 2019 

	Table of Contents

	#General and Typography
	#Preloader
	#Navigation
	#Slider
	#Home page
    #Services pages 	
	#About Pages
	#Team pages
	#Image galleries
	#Contact Pages
	#Blog pages
	#404 page
	#Call to action
    #Footer
	#Miscellaneous
	#Media Queries

/*=========================== General and Typography ============================ */

body {
	font-family: 'Source Sans pro', sans-serif;
	color: #6A6A71;
	font-size: 17px;
	line-height: 1.7;
	overflow-x: hidden;
}

p {
	font-size: 17px;
	line-height: 1.7;
	font-weight: 400;
}

h1 {
	font-size: 3.2em;
	line-height: 1.1em;
	color: #151515;
	font-family: 'Nunito', sans-serif;
	font-weight: 900;
	text-transform: uppercase;
}

h2 {
	color: #151515;
	font-size: 2.8em;
	font-weight: 800;
	font-family: Nunito;
	line-height: 1.0;
}

h3 {
	font-family: 'Nunito', sans-serif;
	font-size: 2.2em;
	color: #151515;
	font-weight: 600;
	margin-bottom: 20px;
	line-height: 1.1;
}

h4 {
	color: #404044;
	font-size: 1.8em;
	margin-bottom: 20px;
	font-weight: 600;
}

h5 {
	color: #414242;
	font-size: 1.5em;
	font-weight: 600;
	margin-bottom: 20px;
}

h6 {
	color: #0E1B23;
	font-family: 'Nunito', sans-serif;
	font-size: 1.3em;
	font-weight: 500;
	margin-bottom: 20px;
}

.h7 {
	color: #949393;
	font-family: 'Nunito', sans-serif;
	font-size: 0.95em;
	font-weight: 900;
	margin-bottom: 20px;
	line-height: 1.3;
	letter-spacing: 1px;
	text-transform: uppercase;
	display: block;
}

.lead {
	font-family: 'Nunito', sans-serif;
	line-height: 1.3em;
}

.school-banner{
	width: 100%;
	height: 50px;
}


/* subtitle */

.subtitle:after {
	z-index: 0;
	content: "\f303";
	font-family: "Font Awesome 5 Free";
	font-weight: 900;
	font-size: 26px;
	transform: rotate(20deg);
	display: block;
}

.subtitle,
p.subtitle {
	display: inline;
	font-size: .93em;
	letter-spacing: 1.5px;
	font-weight: 700;
	text-transform: uppercase;
	margin-bottom: 0px;
	border-bottom: 1px dashed;
	padding-bottom: 20px;
	font-family: Nunito;
	color: #949393;
}


/* Links */

a {
	transition: color .2s ease-in-out;
}

a:hover,
a:focus {
	text-decoration: none;
}

*:focus {
	outline-style: none;
	box-shadow: none;
	border-color: transparent;
}


/* Custom Link */

.custom-link {
	font-family: 'Nunito', sans-serif;
	margin-top: 0px;
	display: inline-block;
	margin: 10px;
	padding: 5px 20px;
	border-radius: 0px;
	font-weight: 700;
	font-size: 16px;
	border: 3px solid;
	letter-spacing: 0.5px;
	transition: all .2s ease-in-out;
	border-radius: 20px;
}

.custom-link:hover {
	background: #f5f5f5;
}

.custom-link:before {
	transition: all .2s ease-in-out;
	display: inline-block;
	font-size: 18px;
	font-family: "Font Awesome 5 Free";
	font-weight: 900;
	content: "\f0a5";
	margin-right: 10px;
}

.custom-link a {
	color: #0E1B23;
}


/*=========================== Preloader ============================ */

#preloader {
	position: fixed;
	top: 0;
	background: #fff;
	left: 0;
	width: 100%;
	height: 100%;
	z-index: 99999;
}

.preloader-logo {
	text-align: center;
	width: 200px;
	animation: rotate 2.0s infinite linear;
}

.spinner {
	margin: 100px auto;
	width: 40px;
	height: 40px;
	position: relative;
	text-align: center;
	-webkit-animation: sk-rotate 2.0s infinite linear;
	animation: sk-rotate 2.0s infinite linear;
}

.dot1,
.dot2 {
	width: 60%;
	height: 60%;
	display: inline-block;
	position: absolute;
	top: 0;
	border-radius: 100%;
	-webkit-animation: sk-bounce 2.0s infinite ease-in-out;
	animation: sk-bounce 2.0s infinite ease-in-out;
}

.dot2 {
	top: auto;
	bottom: 0;
	-webkit-animation-delay: -1.0s;
	animation-delay: -1.0s;
}

@-webkit-keyframes sk-rotate {
	100% {
		-webkit-transform: rotate(360deg)
	}
}

@keyframes sk-rotate {
	100% {
		transform: rotate(360deg);
		-webkit-transform: rotate(360deg)
	}
}

@-webkit-keyframes sk-bounce {
	0%,
	100% {
		-webkit-transform: scale(0.0)
	}
	50% {
		-webkit-transform: scale(1.0)
	}
}

@keyframes sk-bounce {
	0%,
	100% {
		transform: scale(0.0);
		-webkit-transform: scale(0.0);
	}
	50% {
		transform: scale(1.0);
		-webkit-transform: scale(1.0);
	}
}


/*=========================== Navigation ============================ */

.navbar {
	background-color: #fff;
	box-shadow: 1px 5px 10px rgba(49, 49, 49, 0.21);
}

.navbar-nav a {
	font-family: 'Nunito', sans-serif;
	font-weight: 700;
	font-size: 16.5px;
	letter-spacing: 0.5px;
	line-height: 25px;
}

.navbar-nav a {
	color: #404044;
}

.navbar-nav .nav-link {
	padding-right: 1.2rem!important;
	padding-left: 1.2rem!important;
}

.nav-item {
	border-radius: 0px;
	transition: all .2s ease-in-out;
}

.navbar .nav-item:focus .dropdown.show {
	background: transparent!important;
}

.navbar .nav-item.active,
.navbar .nav-item:hover {
	transition: all .4s ease-in-out;
}

.navbar .nav-item.active {
	border-bottom: 6px solid;
}

.navbar .nav-item.active a .dropdown-item {
	border: 0px!important;
}

.navbar .nav-item:hover a {
	color: #fff;
}


/* dropdown style */

.nav-link.dropdown-toggle:focus {
	background: transparent!important;
}

.dropdown-item {
	color: #fff!important;
	transition: all .2s ease-in-out;
	line-height: 45px!important;
}

.dropdown-menu {
	box-shadow: 1px -1px 10px 4px rgba(49, 49, 49, 0.21);
	border: 0px;
	border-radius: 0;
}

.navbar .dropdown-menu a:before {
	display: none;
}


/* navbar brand */

.nav-brand {
	line-height: 20px;
	margin-top: 0px;
}

.nav-brand img {
	max-height: 60px;
}

.navbar-brand i {
	vertical-align: sub;
	margin-right: 10px;
	font-size: 45px;
}

.navbar-toggler {
	transition: all 0.5s ease 0s;
	background: #fff;
	margin-right: 10px;
}
/* colored navbar */

.colored-nav .top-bar {
	background: #fff;
}
.colored-nav .top-bar .contact-details li{color:#222;}

.colored-nav .navbar .nav-item a {
    color: #fff;
}

/* top-bar */

.top-bar {
	transition: all 0.8s;
	top: 0;
	width: 100%;
	position: relative;
}

.top-bar ul,
ol {
	margin-bottom: 0px;
	padding: 0px;
}

.top-bar a {
	color: #fff;
}

.top-bar .contact-details li:first-child {
	padding-left: 0px;
}

.top-bar .contact-details i {
	margin-right: 8px;
	width: 22px;
	height: 22px;
	border-radius: 50%;
	line-height: 22px;
	text-align: center;
	color: #fff;
	font-size: 12px;
}

.top-bar .contact-details li {
	font-size: 13px;
	display: inline-block;
	line-height: 42px;
	font-weight: 700;
	color: #fff;
	padding: 0px 20px;
	font-family: Nunito;
}


/* top-bar Social icons */

ul.social-list {
	list-style-type: none;
}

ul.social-list li a {
	display: inline-block;
	transition: all 0.2s ease-in-out;
}

ul.social-list li a:hover {
	transform: scale(1.2);
}

ul.social-list li a i {
	display: block;
	width: 34px;
	transition: all 0.2s ease-in-out;
	line-height: 42px;
	text-align: center;
	font-size: 18px;
}
ul.social-list.big-icons li a i {
	width: 44px;
	font-size: 25px;
}

ul.social-list li.list-inline-item:not(:last-child) {
	margin: 0px;
}


/* =========================== Slider ============================ */

#slider {
	width: 100% !important;
	height: 800px;
}

.header-wrapper {
	width: 100%!important;
	margin-left: 0%!important;
}

.header-wrapper .header-text {
	margin-top: 15%;
	white-space: normal;
}

.ls-outline .ls-nav-prev,
.ls-outline .ls-nav-next {
	z-index: 99!important;
}


/* dark overlay in slider */

.slider-full .ls-bg-wrap:after,
.ls-slide-transition-wrapper:before {
	transition: 0.8s ease-in-out;
	content: "";
	width: 100%;
	height: 100%;
	z-index: 999;
	top: 0;
	left: 0;
	position: absolute;
	background: -moz-linear-gradient(right, rgba(0, 0, 0, 0.65) 0%, rgba(0, 0, 0, 0) 100%);
	/* FF3.6-15 */
	background: -webkit-linear-gradient(right, rgba(0, 0, 0, 0.65) 0%, rgba(0, 0, 0, 0) 100%);
	/* Chrome10-25,Safari5.1-6 */
	background: linear-gradient(to left, rgba(0, 0, 0, 0.65) 0%, rgba(0, 0, 0, 0) 100%);
	/* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}

/* header text wrapper */

.header-text {
	margin-left: 50%;
	text-align: center;
	width: 50%;
}

.parallax-slider .header-text {
	margin-left: 5%;
	width: 40%!important;
	margin-top: 15%;
}

.header-text h1 {
	text-transform: none;
	font-size: 63px;
	font-weight: 900;
	letter-spacing: 0.5px;
	line-height: 1.1;
	margin-bottom: 15px;
	color: #222;
}

.header-text span {
	color: #222;
	font-weight: 700;
	font-size: 40px;
	font-family: Nunito;
}

.header-p {
	font-weight: 500;
	font-size: 22px;
	line-height: 1.3;
	margin-bottom: 15px;
}
/* ===========================
 Drag Slider
============================ */

.slider-container {
    overflow: hidden;
    position: relative;
    cursor: all-scroll;
}
.slider {
    z-index: 1;
    position: relative;
    height: 700px;
}
/*Controls */

.slider-control {
    z-index: 2;
    position: absolute;
    top: 0;
    width: 12%;
    height: 100%;
    transition: opacity 0.3s;
    opacity: 0;
}
.slider-control.inactive:hover {
    cursor: auto;
}
.slider-control:not(.inactive):hover {
    opacity: 1;
    cursor: pointer;
}
.slider-control.left {
    left: 0;
    background: linear-gradient(to right, rgba(0, 0, 0, 0.18) 0%, rgba(0, 0, 0, 0) 100%);
}
.slider-control.right {
    right: 0;
    background: linear-gradient(to right, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.18) 100%);
}
/* Pagination */

ul.slider-pagi {
    padding-left: 0px;
}
.slider-pagi {
    position: absolute;
    z-index: 999;
    left: 50%;
    bottom: 1rem;
    transform: translateX(-50%);
    font-size: 0;
    list-style-type: none;
    bottom: -10px;
}
.slider-pagi__elem {
    border: 2px solid #fff;
    position: relative;
    display: inline-block;
    vertical-align: top;
    width: 1rem;
    height: 1rem;
    margin: 0 0.5rem;
    border-radius: 50%;
    cursor: pointer;
}
.slider-pagi__elem:before {
    content: "";
    position: absolute;
    left: 50%;
    top: 50%;
    background: #cecece;
    width: 1.2rem;
    height: 1.2rem;
    border-radius: 50%;
    transition: transform 0.3s;
    transform: translate(-50%, -50%) scale(0);
}
.slider-pagi__elem.active:before,
.slider-pagi__elem:hover:before {
    transform: translate(-50%, -50%) scale(1);
}
/* Slider Animation */

.slider.animating {
    transition: transform 0.5s;
}
.slider.animating .slide__bg {
    transition: transform 0.5s;
}
.slide {
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    overflow: hidden;
    background-size: cover;
    background-position: center;
}
.slide.active .slide__overlay,
.slide.active .slide__text {
    opacity: 1;
    transform: translateX(0);
}
.slide__bg {
    position: absolute;
    top: 0;
    left: -50%;
    width: 100%;
    height: 100%;
    background-size: cover;
    background-position: top right;
}
.slide__content {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}
/* Slider Overlay */

.slide__overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  height: 100%;
  min-height: 610px;
  -webkit-transition: -webkit-transform 0.5s 0.5s, opacity 0.2s 0.5s;
          transition: transform 0.5s 0.5s, opacity 0.2s 0.5s;
  -webkit-transform: translate3d(-20%, 0, 0);
          transform: translate3d(-20%, 0, 0);
  opacity: 0;
}

.slide__overlay path {opacity: 0.4;}
/* Slider Typography */

.slide__text {
    text-align: center;
    position: absolute;
    max-width: 450px;
    bottom: 24%;
    left: 20px;
    color: #fff;
    transition: transform 0.5s 0.5s, opacity 0.2s 0.5s;
    transform: translate3d(-20%, 0, 0);
    opacity: 0;
}
.slide__text h1 {
    color: #fff;
    text-transform: uppercase;
}
.slide__text-heading {
    margin-top: 0;
}
.lead {
    font-size: 1.4rem;
    font-weight: normal;
    margin: 1.1rem 0;
}
/* slide1 */

.slide:nth-child(1) {
    left: 0;
}
/* slide2 */

.slide:nth-child(2) {
    left: 100%;
}
/* slide3 */

.slide:nth-child(3) {
    left: 200%;
}
/* slide4 */

.slide:nth-child(4) {
    left: 300%;
}
/* slide5 */

.slide:nth-child(5) {
    left: 400%;
}
/* slide6 */

.slide:nth-child(6) {
    left: 500%;
}
/* slide7 */

.slide:nth-child(7) {
    left: 600%;
}
/* slide8 */

.slide:nth-child(8) {
    left: 700%;
}
/*=========================== Home page ============================ */


/* Flip cards */

#intro-cards .card {
	padding: 0;
}

#intro-cards .card-flip .card {
	-webkit-backface-visibility: hidden;
	backface-visibility: hidden;
	transition: -webkit-transform 300ms;
	transition: transform 300ms;
	transition: transform 300ms, -webkit-transform 300ms;
	transition-timing-function: linear;
	width: 100%;
	height: 100%;
	margin: 0;
}

#intro-cards .card-img {
	width: 100%;
	border-radius: 30% 70% 15px 15px;
}

#intro-cards .card-flip.card {
	background: none;
}

#intro-cards .card-flip .card .btn {
	background-color: 0;
	border: 2px solid #fff;
}

#intro-cards .card-flip .card:first-of-type {
	transform: rotateY(0deg);
}


/* Whale in the sea animation */

.floating-whale {
	left: 50%;
	z-index: 0;
	-webkit-animation: float 6s ease-in-out infinite;
	animation: float 6s ease-in-out infinite;
	margin-bottom: -45px;
	margin-left: 120px;
}

.sea {
	height: 50px;
	position: relative;
	z-index: 99;
}


/* Waves animation */

.waveHorizontals {
	width: 100%;
	height: 20px;
	position: relative;
	bottom: 0;
	z-index: 9;
}

.waveHorizontal {
	width: 100%;
	height: 100%;
	display: block;
	position: absolute;
	background-repeat: repeat-x;
	-webkit-animation: move 12s linear infinite;
	animation: move 12s linear infinite;
}

@keyframes float {
	0% {
		-webkit-transform: translatey(0px);
		transform: translatey(0px);
	}
	50% {
		-webkit-transform: translatey(-10px);
		transform: translatey(-10px);
	}
	100% {
		-webkit-transform: translatey(0px);
		transform: translatey(0px);
	}
}

#waveHorizontal1 {
	background-size: 100px 100px;
	opacity: 0.3;
}

#waveHorizontal2 {
	-webkit-animation-delay: -2s;
	animation-delay: -2s;
	-webkit-animation-duration: 12s;
	animation-duration: 12s;
	opacity: 0.5;
}

#waveHorizontal3 {
	-webkit-animation-delay: -4s;
	animation-delay: -4s;
	-webkit-animation-duration: 8s;
	animation-duration: 8s;
}

@keyframes move {
	0% {
		background-position: left 0px bottom 0;
	}
	100% {
		background-position: left 350px bottom 0;
	}
}


/*=========================== Services pages ============================ */


/* Services style2 */

.serviceBox2 {
	text-align: center;
	padding: 20px;
	background: #f5f5f5;
	border-radius: 20px;
	transition: all .4s ease-in-out;
}

.serviceBox2 .service-content {
	padding: 20px;
}

.serviceBox2 .service-icon img {
	margin-bottom: 20px;
	border: 15px solid transparent;
}

.serviceBox2:hover .service-icon img {
	border: 15px solid;
}

.serviceBox2 .service-head {
	transition: all .4s ease-in-out;
}


/*=========================== About pages ============================ */


/* testimonials */

.testimonial {
	background-color: #faf8d4;
	padding: 40px 40px 20px 70px;
	position: relative;
}

.testimonial:before {
	content: "";
	width: 0;
	height: 0;
	border-bottom: 50px solid #eee;
	border-left: 50px solid #fff;
	box-shadow: 7px 7px 7px rgba(0, 0, 0, 0.17);
	position: absolute;
	top: 0;
	left: 0;
}

.testimonial .description:before {
	content: "\f10d";
	font-family: "Font Awesome 5 Free";
	font-weight: 900;
	display: inline-block;
	float: left;
	padding: 0 15px 5px 0;
	font-size: 30px;
	text-align: center;
	position: relative;
	left: 0;
}

.testimonial .testimonial-title {
	font-size: 22px;
	margin-bottom: 5px;
	text-align: center;
}

.testimonial .post {
	display: block;
	font-size: 18px;
	font-weight: 700;
	text-align: center;
}

.testimonial .description {
	font-family: Nunito;
}

.testimonial .testimonial-review {
	padding-top: 20px;
}

.testimonial .testimonial-pic {
	width: 100px;
	overflow: hidden;
	margin: 0 auto;
	border-radius: 30% 70% 70% 30% / 30% 45% 55% 70%;
}


/* number counter */

.counter {
	text-align: center;
}

.counter-wrapper {
	border-radius: 10px;
	box-shadow: -2px 1px 20px rgba(0, 0, 0, 0.49);
}

.counter .counter-icon {
	display: block;
	font-size: 70px;
	transition: all ease-in-out 0.2s;
	color: #fff;
	border-radius: 50%;
	opacity: 0.5;
}

.counter:hover .counter-icon {
	transform: scale(1.2);
}

.counter .counter-value {
	display: block;
	font-family: 'Nunito', sans-serif;
	font-weight: 700;
	font-size: 40px;
	color: #fff;
	letter-spacing: 2px;
}

.counter .counter-value:before {
	content: "+";
	display: inline-block;
	font-family: 'Nunito', sans-serif;
	font-weight: 700;
	font-size: 30px;
	color: #fff;
	margin-right: 5px;
}

.counter .title {
	color: #fff;
	font-size: 22px;
	letter-spacing: 0.5px;
	width: 100%;
	display: inline-block;
}


/* Features with icon */

.icon-features i {
	font-size: 55px;
	display: inline-block;
}

.icon-features {
	border-radius: 73% 34% 44% 35% / 43% 42% 41% 39%;
	background: #ffffff;
	transition: all 0.4s ease-in-out;
	margin-bottom: 20px;
	display: inline-block;
}

.feature-with-icon:hover .icon-features {
	transform: rotate(20deg);
	border-radius: 50%;
}

.feature-with-icon i {
	transition: all 0.4s ease-in-out;
	text-align: center;
	width: 100px;
}

.feature-with-icon {
	text-align: center;
}

.feature-with-icon h5 {
	margin-bottom: 10px;
}


/*=========================== Team pages ============================ */


/* team style 1 */

.team-style1 {
	text-align: center;
}

.team-style1 .team_img {
	position: relative;
	overflow: hidden;
}

.team-style1 img {
	border-radius: 50%;
	padding: 20px;
}

.team-style1 ul.social {
	margin: 0;
	padding: 0;
}

.team-style1 .social li {
	width: 30px;
	height: 30px;
	text-align: center;
	margin: 2px;
	padding: 5px;
	display: inline-block;
}

.team-style1 .team-content {
	padding: 10px 0;
}

.team-style1 .team-content p {
	padding: 20px 0px;
}

.team-style1 .title {
	margin: 10px 0 5px;
	position: relative;
	font-size: 24px;
	font-weight: 700;
	transition: all ease-in-out 0.2s;
}

.team-style1 .post {
	font-size: 18px;
	font-weight: 600;
	border-bottom: 1px solid;
}

.team-style1:before {
	content: "\f303";
	transition: all ease-in-out 0.2s;
	position: absolute;
	font-family: "Font Awesome 5 Free";
	font-weight: 900;
	font-size: 30px;
	right: 0px;
	transform: rotate(20deg);
	z-index: 0;
	bottom: 0px;
}

.team-style1:hover:before {
	font-size: 40px;
	transform: rotate(0deg);
}


/* team style 2 */

.team-style2 {
	background: #fff;
	border-radius: 10px;
	padding: 20px;
