	text-align: center;
	margin-right: 10px;
	vertical-align: sub;
}

.nav-tabs .nav-item:hover i {
	text-shadow: 0px 2px 30px #f3ebeb;
	color: #fff;
	-moz-transform: translate(0px, 0);
	-moz-transition: -moz-transform 0.8s ease;
	transform: rotate(14deg) scale(1.3);
	transition: -webkit-transform 0.8s ease;
}

.nav-tabs .nav-item,
.nav-tabs,
.nav-tabs .nav-item.show .nav-link,
.nav-tabs .nav-link.active {
	border: 0px;
}

.nav-tabs .nav-link:focus,
.nav-tabs .nav-link,
.nav-tabs .nav-link.active,
.nav-tabs .nav-link:hover {
	color: #fff;
}

.tab-content {
	background: #fff;
	padding: 50px;
	border: 0px;
	box-shadow: 1px 2px 20px rgba(105, 105, 105, 0.21);
}

.tab-content.block-padding:last-child {
	padding-bottom: 70px;
}


/* tab animation on active */

.tab-pane.active {
	animation: slide-down 0.5s ease-out;
}

@keyframes slide-down {
	0% {
		opacity: 0;
		transform: translatey(-20px);
	}
	100% {
		opacity: 1;
		transform: translatey(0);
	}
}


/* centered navs */

.center-nav {
	display: flex;
	justify-content: center;
}


/* cards */

.card {
	background: #f5f5f5;
	display: block;
	border: 0px;
	border-radius: 15px;
	padding: 30px;
}

.card-header {
	background: none;
}

.card-title {
	transition: all 0.2s ease-in-out;
}

a .card-title:hover {
	color: inherit;
}


/* widgets */

.widget-area {
	margin: 40px 0px;
}

.widget-area:first-child {
	margin-top: 0px;
}

.widget1 a,
.widget2 a {
	transition: all 0.2s ease-in-out;
}

.widget1 img {
	max-width: 60px;
	vertical-align: middle;
}

.widget1 span {
	font-size: 20px;
	font-weight: 600;
	margin-left: 20px;
}

.widget-1-info {
	margin-top: 10px;
	background: #fff;
	border-radius: 10px;
}

.widget2 .card {
	padding: 0px;
	background: transparent;
}

.widget2 .card img {
	transition: all 0.4s ease-in-out;
}

.widget2 .card .card-img {
	overflow: hidden;
}

.widget2 .card:hover img {
	transform: scale(1.2);
}


/* tables */

.table thead th {
	vertical-align: bottom;
	font-size: 18px;
}

.table thead {
	color: #fff;
}


/* accordions */

.accordion-cards .card {
	border: 0px;
	display: block;
	padding: 0px;
	border-radius: 0px;
	background: #fff;
}

.accordion .card-link {
	display: block;
	padding: 5px 20px;
	border-radius: 5px;
	border-bottom: 0;
	transition: all 0.8s;
	margin: 5px;
	cursor: pointer;
}

.accordion .card-link,
.accordion .card-link.collapsed,
.accordion .card-link:focus {
	color: #ffffff!important;
	transition: all 0.8s;
}

.accordion-cards .card-body {
	margin: 0 20px;
	padding: 40px;
	border-radius: 5px;
}

.accordion .card {
	padding: 0px;
	background: transparent;
}

.accordion .card-header {
	font-weight: 700;
	font-size: 17px;
	padding: 0px;
	line-height: 1.3;
	color: #fff;
	border-radius: 5px;
	margin-bottom: px;
	letter-spacing: 0.7px;
	font-family: Nunito;
	border: 0;
}

.accordion {
	overflow: hidden;
	position: relative;
	z-index: 1;
}

.accordion a:before {
	content: "\f068";
	font-family: "Font Awesome 5 Free";
	font-weight: 900;
	width: 40px;
	height: 40px;
	color: #fff;
	line-height: 40px;
	border-radius: 50%;
	text-align: center;
	font-size: 14px;
	top: 0;
	left: -75px;
	background: transparent;
	margin-right: 10px;
}

.accordion .card-header a.collapsed:before {
	content: "\f067";
}


/* img-fluid */

.img-fluid {
	max-width: 100%;
}


/* light background */

.bg-light {
	background-color: #f5f5f5!important;
}


/* full-container */

.container-fluid {
	padding-right: 0px;
	padding-left: 0px;
}


/* section */

section {
	padding-top: 90px;
	padding-bottom: 90px;
}


/* no-padding */

.no-padding {
	padding-top: 0px;
	padding-bottom: 0px;
}


/* section-heading */

.section-heading {
	margin-bottom: 70px;
}

.section-heading h2 {
	font-size: 62px;
	font-weight: 900;
}


/* custom ul */

ul.custom {
	list-style: none;
	margin-bottom: 0;
}

ul.custom li {
	padding: 5px 0px;
}

ul.custom li:before {
	content: "\f303";
	font-family: "Font Awesome 5 Free";
	font-weight: 900;
	padding-right: 7px;
}


/* checkmark ul */

ul.checkmark {
	list-style: none;
	margin-bottom: 0;
}

ul.checkmark li {
	padding: 5px 0px;
	font-size: 16px;
	line-height: 26px;
}

ul.checkmark li:before {
	font-family: "Font Awesome 5 Free";
	content: "\f14a";
	padding-right: 7px;
}


/* white border if bg same color as form border */

.bg-primary .form-control {
	border-color: #fff;
}


/* notepad */

.notepad {
	background: #f6f6f6;
	box-shadow: 0 1px 4px hsla(0, 0%, 0%, .25);
	position: relative;
	background-image: repeating-linear-gradient(to bottom, transparent 0, transparent 30px, rgba(173, 216, 230, 0.31) 30px, rgba(173, 216, 230, 0.31) 32px), linear-gradient(to right, transparent 0, transparent 2rem, rgba(255, 182, 193, 0.48) 2rem, rgba(255, 182, 193, 0.48) 2.2rem, transparent 2.2rem);
	padding: 30px 20px 30px 40px;
}

.notepad p {
	color: #3c3b3b;
}


/* img zoom on hover */

.img-zoom-hover {
	overflow: hidden;
}

.img-zoom-hover img {
	transition: all 0.2s ease-in-out;
}

.img-zoom-hover:hover img {
	transform: scale(1.1);
}


/* rotate image */

.rotate1 {
	transform: rotate(-2deg);
}


/* block padding */

.block-padding {
	padding-top: 70px;
	padding-bottom: 70px;
}

.block-padding:first-child {
	padding-top: 0px;
}

.block-padding:last-child {
	padding-bottom: 0px;
}

.block-padding:first-child.force {
	padding-top: 70px;
}

.block-padding:last-child.force {
	padding-bottom: 70px;
}

.block-padding-top {
	padding-top: 70px;
}


/* blob shape 1 */

.blob {
	border-radius: 30% 70% 70% 30% / 30% 45% 55% 70%;
	transition: all 0.7s ease-in-out;
}

.blob:hover {
	border-radius: 57% 43% 31% 69% / 54% 61% 39% 46%;
}


/* blob shape 2 */

.blob2 {
	border-radius: 62% 38% 82% 18% / 54% 61% 39% 46%;
	transition: all 0.7s ease-in-out;
}

.blob2:hover {
	border-radius: 30% 70% 70% 30% / 30% 45% 55% 70%;
}


/* moving blob */

.moving-blob {
	animation: morph 4s ease-in-out infinite both alternate;
}

@keyframes morph {
	0% {
		border-radius: 30% 70% 70% 30% / 30% 45% 55% 70%;
	}
	100% {
		border-radius: 57% 43% 31% 69% / 54% 61% 39% 46%;
	}
}


/* overlay background */

.overlay {
	box-shadow: inset 0 0 0 1222px rgba(41, 41, 41, 0.42);
}

.overlay-light {
	box-shadow: inset 0 0 0 1222px rgba(255, 255, 255, 0.82);
}

.overlay-dark {
	box-shadow: inset 0 0 0 1222px rgba(41, 41, 41, 0.87);
}


/* extra paddings and margins */

.p-10 {
	padding: 100px;
}

.p-20 {
	padding: 200px 0;
}

.pb-8 {
	padding-bottom: 80px;
}

.mt-8 {
	padding-top: 80px;
}


/* sticky top */

.sticky-top {
	top: 100px;
}


/* list group */

.list-group {
	font-weight: 700;
	font-size: 18px;
}

.list-group-item {
	font-family: 'Nunito', sans-serif;
	transition: all 0.2s ease-in-out;
	margin-bottom: 4px;
	border: 0;
	background: none;
	border-radius: 5px!important;
	font-size: 18px;
	border-bottom: 2px solid transparent;
}

.list-group-item.active,
.list-group-item-action:focus,
.list-group-item-action:hover {
	background: none;
	border-bottom: 2px solid;
}

.list-group-item.active:hover,
.list-group-item-action:hover {
	color: #fff;
}


/* Pagination*/

.pagination {
	margin-bottom: 0px;
}


/* Jumbotron */

.jumbotron {
	background: url("../img/jumbotron.jpg");
	background-repeat: no-repeat;
	background-attachment: fixed;
	background-size: contain;
	margin-bottom: 0px;
	background-position:center top;
}

.jumbotron .breadcrumb {
	max-width: 400px;
	border-radius: 0px;
}

.jumbo-heading {
	margin-bottom: 0px;
	text-align: left;
	text-align: center;
	border-radius: 10px;
	padding: 20px 40px;
	margin-top: 70px;
	display: table;
	margin-left: auto;
	margin-right: auto;
}

.jumbo-heading h1 {
	color: #fff;
	font-size: 2.5em;
}


/* Owl-carousel */
 .owl-height {
     transition: height 500ms ease-in-out;
}
.owl-prev,
.owl-next {
	position: absolute;
	top: 50%;
	text-align: center;
}

.owl-nav {
	margin: 0 auto;
	top: 0px;
	left: 50%;
}
.owl-theme .owl-controls .owl-page {
     display: inline-block;
}
.owl-prev {
	left: -10px;
}

.owl-next {
	right: -10px;
}

.owl-prev i,
.owl-next i {
	transition: all .3s ease-in-out;
	font-size: 14px;
	border-radius: 50%;
	width: 30px;
	height: 30px;
	line-height: 30px;
	color: #fff;
	box-shadow: -4px -2px 20px rgba(0, 0, 0, 0.39);
}

.owl-prev i:hover,
.owl-next i:hover {
	transform: scale(1.2);
}

.owl-carousel.owl-loaded {
	display: block;
	clear: both;
}

.owl-dots {
	text-align: center;
	margin-top: 10px;
}

.owl-carousel .owl-stage-outer {
	margin-top: -7px;
}

.owl-theme .owl-dots .owl-dot {
	display: inline-block;
}

.owl-theme .owl-dots .owl-dot span {
	border: 2px solid #cecece;
	position: relative;
	display: inline-block;
	vertical-align: top;
	width: 0.6rem;
	height: 0.6rem;
	margin: 0 0.3rem;
	border-radius: 50%;
	cursor: pointer;
	color: #333;
	transition: all 0.3s ease-in-out;
}

.owl-carousel  {
	margin-top: 40px;
}

.owl-carousel.mt-0 .owl-stage {
	margin-top: 0px!important;
}

/* Owl-carousel */
 .owl-theme .owl-controls .owl-page {
     display: inline-block;
}

 .owl-carousel .owl-item {
     perspective: 1000px;
}



/* owl carousel with navigation on top */

.top-centered-nav .owl-nav {
	position: absolute;
}

.top-centered-nav .owl-prev,
.top-centered-nav .owl-next {
	top: 0%;
}

.top-centered-nav .owl-prev {
	left: -35px;
}

.top-centered-nav .owl-next {
	right: -35px;
}


/* Mailchimp message */

#mce-error-response,
#mce-success-response {
	display: none;
	margin-top: 20px;
}


/* Page margin*/

.page {
	background-color: #fff;
	padding-top: 120px;
	padding-bottom: 70px;
}


/* opacity on hover */

.hover-opacity {
	transition: all 0.4s ease-in-out;
}

.hover-opacity:hover {
	opacity: 0.6;
}


/* border hover */

.hover-border img {
	transition: all 0.4s ease-in-out;
}

.hover-border:hover img {
	border: 15px solid;
}


/* alerts */

.alert {
	padding: 20px;
	border-radius: 15px;
}

.alert.alert-primary:before,
.alert.alert-secondary:before {
	color: #fff;
}

.alert:before {
	font-family: "Flaticon";
	content: "\f131";
	font-weight: normal;
	position: absolute;
	float: right;
	right: 10px;
	top: -10px;
	font-size: 50px;
	transform: rotate(10deg);
	opacity: 0.2;
	z-index: 0;
}

.alert-primary,
.alert-secondary {
	color: #fff;
}


/* nav pills */

.nav-pills .nav-link {
	font-size: 18px;
	color: #fff;
	border-radius: 10px;
	padding: 10px 40px;
	margin: 5px 10px;
	transition: all 0.2s ease-in-out;
	font-family: 'Nunito', sans-serif;
	font-weight: 900;
	text-transform: uppercase;
	letter-spacing: 1px;
}

.nav-link.disabled {
	color: #6c757d!important;
}


/* pills /badges */

.badge-pill {
	padding: 10px;
	margin: 3px;
}

.badge-default {
	color: #fff;
}


/* breadcrumbs */

.breadcrumb {
	margin-bottom: 0;
	color: #fff;
	background: transparent;
	font-size: 14px;
	display: block;
	padding: 0px;
	text-align: center;
	overflow: hidden;
	text-overflow: ellipsis;
	white-space: no-wrap;
	font-weight: 600;
	letter-spacing: 1px;
}

.breadcrumb .breadcrumb-item {
	display: inline;
}

.breadcrumb a,
.breadcrumb li a {
	color: #fff;
}

.breadcrumb-item.active {
	color: #b1b5b9;
}

.breadcrumb-item+.breadcrumb-item::before {
	color: #fff;
	font-size: 7px;
	font-family: "Font Awesome 5 Free";
	font-weight: 900;
	content: "\f054";
}


/* pagination */

.page-link:hover,
.page-link:focus,
.page-link.active {
	color: #fff;
	border: 1px solid #fff;
	outline: 0;
}

.page-link {
	transition: all 0.2s ease-in-out;
	font-family: 'Nunito', sans-serif;
	color: #fff;
	font-weight: 700;
	border: 1px solid #fff;
}


