	}
	.ornament-stars:after,
	.ornament-bubbles:after,
	.ornament-rainbow:after {
		position: relative;
	}
	#intro-cards .card-flip {
		margin-top: 20px;
	}
	.card-back {
		display: none;
	}
	.nav-tabs .nav-link {
		width: 100%;
		margin-right: 0px;
		text-align: center;
	}
	#divider {
		display: none;
	}
	.contact-form3 {
		margin-top: 55px;
	}
	.bg-collumn {
		min-height: 300px;
	}
	.nav-tabs .nav-link {
		padding: 15px 30px;
		border-radius: 0px;
	}
	#sidebar {
		margin-top: 50px;
	}
	/* no background or hidden on small devices */
	.no-bg-small {
		background-image: none!important;
	}
	.hidden-medium-small {
		display: none;
	}
	 /* Drag Slider */

    .slider {
        height: 400px;
    }
    .slide .slide__text {
        background-color: rgba(0, 0, 0, 0.4);
    }
    .slide__text {
        left: 0;
		max-width: 100%;
        bottom: 0px;
        width: 100%;
		word-break:break-word;
        text-align: center;
        transform: translateY(50%);
        transition: transform 0.5s 0.5s, opacity 0.5s 0.5s;
        padding: 40px;
    }
    .slide__overlay {
        display: none;
    }
    .slide__text-heading {
        font-size: 1.5rem;
    }
    .slider-pagi__elem,.slider-pagi__elem:before {
		width: 0.5rem;
		height: 0.5rem;	
    }
	/* widget */
	.widget2 {
		margin-top: 30px;
	}
	.widget2:first-child {
		margin-top: 0px;
	}
	/* slider */
	.header-text:before {
		font-size: 50px!important;
		margin-top: 30px;
	}
	/*top bar */
	.top-bar .contact-details li {
		font-size: 13px;
	}
	.top-bar .contact-details li {
		margin: 0px!important;
	}
	.top-bar .contact-details i {
		margin-right: 8px;
		margin-left: 8px;
	}
	/* others */
	.res-margin {
		margin-top: 30px;
	}
	.img-rotate-outline {
		padding: 30px;
		border: 0px;
	}
	.navbar-collapse.show {
		max-height: 400px;
	}
	.navbar-collapse {
		max-height: 400px;
	}
	.navbar-collapse::-webkit-scrollbar {
		width: 10px;
	}
	.navbar-collapse::-webkit-scrollbar-track {
		background-color: lightgray;
	}
	.navbar-collapse::-webkit-scrollbar-thumb {
		background-color: darkgray;
	}
	.navbar-collapse.show {
		overflow-y: scroll!important;
		overflow-x: hidden!important;
	}
}


/* Large devices (large desktops, min 1200px ) The navbar toggle appears at this breakpoint*/

@media (min-width: 1200px) {
	.navbar-expand-xl .navbar-nav .dropdown-menu {
		position: absolute;
		margin-top: 0px;
		padding: 0px;
	}
	#intro-cards {
		margin-top: -300px;
		position: relative;
		z-index: 99;
	}
	#intro-cards.cards-no-margin{
		margin-top: 0px;
	}
	/* vertical-tabs */
	.vertical-tabs .nav.nav-tabs {
		float: left;
		display: inline-block;
		margin-right: 0;
		border-bottom: 0;
		border-right: 1px solid #ddd;
	}
	.vertical-tabs nav .nav-tabs {
		display: inline-block;
		width: 25%;
	}
	.vertical-tabs .tab-content {
		float: right;
		width: 75%;
		display: inline-block;
	}
	.vertical-tabs .nav-tabs .nav-link {
		margin-right: 0px;
		border-top-left-radius: 0;
		border-top-right-radius: 0;
		padding: 10px 20px;
		margin-bottom: 20px;
		border-radius: 10px 0px 0px 10px;
		text-align: center;
	}
	/* jumbotron */
	.jumbotron {
		height: 200px;
	}
	/* navigation */
	.navbar-nav .nav-link {
		line-height: 70px;
	}
	.navbar-nav .nav-item.active .nav-link {
		line-height: 65px;
	}
	.navbar {
		height: 86px;
		padding: 0px;
	}
	.dropdown-menu {
		margin-top: 30px;
	}
}

@media (max-width: 1200px) {
	.top-bar .contact-details li {
		padding: 0px 0px;
	}
	#not-found {
		margin-top: 5rem;
		margin-bottom: 3rem;
	}
	.counter {
		margin-bottom: 20px;
	}
	/* Jumbotron */
	.jumbotron {
		min-height: 250px;
		background-size: cover!important;
		background-attachment: scroll;
	}
	/* navigation */
	.dropdown-menu:before {
		display: none;
	}
	.navbar .nav-item {
		margin-top: 20px;
	}
	.navbar-nav .dropdown-menu {
		float: left;
		width: 100%;
	}
	.navbar-toggler:hover i {
		color: #fff;
	}
	.navbar {
		min-height: 50px;
		padding: 0px;
	}
	.nav-brand img {
		max-height: 50px;
		padding: 5px;
	}
	.dropdown-item,
	.navbar-nav .nav-link {
		text-align: center;
	}
	.dropdown-item {
		padding: 0.8rem;
	}
	.navbar-collapse.show {
		display: block;
		padding-bottom: 40px;
	}
	.header-text {
		width: 50%!important;
		position: absolute;
		margin-left: 40%;
	}
	.header-text.full-width {
		width: 100%!important;
		margin-left: 2%;
		padding: 0px!important;
		margin-top: 10%!important;
	}
}


/* Very Large devices ( min 1500px ) */

@media (min-width: 1500px) {
	.jumbotron {
		height: 280px;
	}
	.jumbo-heading {
		margin-top: 140px;
	}
	.slider-divider {
		margin-top: -100px;
	}
}
